import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

const Navbar = () => {
  const { user, logout, isAuthenticated } = useAuth();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
    setIsMobileMenuOpen(false);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <nav 
      className="sticky top-0 z-50" 
      style={{ 
        backgroundColor: 'var(--bg-secondary)', 
        boxShadow: 'var(--shadow-lg)' 
      }}
    >
      <div className="container-main">
        <div className="flex justify-between items-center py-4">
          
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-3 fade-in group">
            <div className="relative">
              <span className="text-4xl filter drop-shadow-lg transition-transform duration-300 group-hover:scale-110">🎬</span>
              <div className="absolute inset-0 bg-gradient-to-r from-yellow-400 to-yellow-600 rounded-full blur opacity-30 group-hover:opacity-60 transition-opacity duration-300"></div>
            </div>
            <span 
              className="text-2xl font-bold bg-gradient-to-r from-yellow-400 via-yellow-500 to-yellow-600 bg-clip-text text-transparent"
              style={{ 
                textShadow: '0 0 20px rgba(255, 215, 0, 0.5)',
                fontFamily: "'Cinzel', serif"
              }}
            >
              CinemaFlix
            </span>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-8">
            <Link 
              to="/movies" 
              className="font-medium transition-all duration-200 hover:transform hover:-translate-y-0.5"
              style={{ 
                color: 'var(--text-secondary)',
                fontSize: 'var(--font-size-base)'
              }}
              onMouseEnter={(e) => e.target.style.color = 'var(--brand-primary)'}
              onMouseLeave={(e) => e.target.style.color = 'var(--text-secondary)'}
            >
              Movies
            </Link>
            <Link 
              to="/events" 
              className="font-medium transition-all duration-200 hover:transform hover:-translate-y-0.5"
              style={{ 
                color: 'var(--text-secondary)',
                fontSize: 'var(--font-size-base)'
              }}
              onMouseEnter={(e) => e.target.style.color = 'var(--brand-primary)'}
              onMouseLeave={(e) => e.target.style.color = 'var(--text-secondary)'}
            >
              Events
            </Link>
            <Link 
              to="/sports" 
              className="font-medium transition-all duration-200 hover:transform hover:-translate-y-0.5"
              style={{ 
                color: 'var(--text-secondary)',
                fontSize: 'var(--font-size-base)'
              }}
              onMouseEnter={(e) => e.target.style.color = 'var(--brand-primary)'}
              onMouseLeave={(e) => e.target.style.color = 'var(--text-secondary)'}
            >
              Sports
            </Link>
          </div>

          {/* User Menu */}
          <div className="hidden md:flex items-center space-x-6">
            {isAuthenticated() ? (
              <div className="flex items-center space-x-6">
                <Link 
                  to="/my-bookings" 
                  className="font-medium transition-all duration-200"
                  style={{ color: 'var(--text-secondary)' }}
                  onMouseEnter={(e) => e.target.style.color = 'var(--brand-primary)'}
                  onMouseLeave={(e) => e.target.style.color = 'var(--text-secondary)'}
                >
                  My Bookings
                </Link>
                <div className="relative group">
                  <button className="flex items-center space-x-3 transition-all duration-200">
                    <span 
                      className="w-10 h-10 rounded-full flex items-center justify-center text-sm font-bold"
                      style={{ 
                        backgroundColor: 'var(--brand-primary)', 
                        color: 'var(--text-inverse)',
                        boxShadow: 'var(--shadow-md)'
                      }}
                    >
                      {user?.name ? user.name.charAt(0).toUpperCase() : 'U'}
                    </span>
                    <span style={{ color: 'var(--text-primary)', fontWeight: 'var(--font-weight-medium)' }}>
                      {user?.name || 'User'}
                    </span>
                  </button>
                  
                  {/* Dropdown Menu */}
                  <div 
                    className="absolute right-0 mt-3 w-52 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200 slide-up"
                    style={{ 
                      backgroundColor: 'var(--bg-secondary)',
                      borderRadius: 'var(--radius-lg)',
                      boxShadow: 'var(--shadow-xl)',
                      border: '1px solid var(--neutral-200)'
                    }}
                  >
                    <div style={{ padding: 'var(--space-sm)' }}>
                      <Link 
                        to="/profile" 
                        className="block px-4 py-3 text-sm font-medium rounded-lg transition-all duration-200"
                        style={{ color: 'var(--text-primary)' }}
                        onMouseEnter={(e) => {
                          e.target.style.backgroundColor = 'var(--neutral-100)';
                          e.target.style.color = 'var(--brand-primary)';
                        }}
                        onMouseLeave={(e) => {
                          e.target.style.backgroundColor = 'transparent';
                          e.target.style.color = 'var(--text-primary)';
                        }}
                      >
                        Profile
                      </Link>
                      {user?.roles?.some(role => role.name === 'ROLE_ADMIN') && (
                        <Link 
                          to="/admin" 
                          className="block px-4 py-3 text-sm font-medium rounded-lg transition-all duration-200"
                          style={{ color: 'var(--text-primary)' }}
                          onMouseEnter={(e) => {
                            e.target.style.backgroundColor = 'var(--neutral-100)';
                            e.target.style.color = 'var(--brand-primary)';
                          }}
                          onMouseLeave={(e) => {
                            e.target.style.backgroundColor = 'transparent';
                            e.target.style.color = 'var(--text-primary)';
                          }}
                        >
                          Admin Dashboard
                        </Link>
                      )}
                      <button 
                        onClick={handleLogout}
                        className="block w-full text-left px-4 py-3 text-sm font-medium rounded-lg transition-all duration-200"
                        style={{ color: 'var(--error)' }}
                        onMouseEnter={(e) => e.target.style.backgroundColor = '#FEF2F2'}
                        onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
                      >
                        Logout
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ) : (
              <div className="flex items-center space-x-4">
                <Link 
                  to="/login" 
                  className="btn-secondary"
                  style={{ textDecoration: 'none' }}
                >
                  Login
                </Link>
                <Link 
                  to="/register" 
                  className="btn-primary"
                  style={{ textDecoration: 'none' }}
                >
                  Sign Up
                </Link>
              </div>
            )}
          </div>

          {/* Mobile Menu Button */}
          <button 
            onClick={toggleMobileMenu}
            className="md:hidden flex items-center justify-center w-8 h-8 text-gray-600"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>

        {/* Mobile Menu */}
        {isMobileMenuOpen && (
          <div className="md:hidden bg-white border-t border-gray-200">
            <div className="py-4 space-y-4">
              <Link 
                to="/movies" 
                className="block text-gray-600 hover:text-blue-600 transition-colors"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Movies
              </Link>
              <Link 
                to="/events" 
                className="block text-gray-600 hover:text-blue-600 transition-colors"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Events
              </Link>
              <Link 
                to="/sports" 
                className="block text-gray-600 hover:text-blue-600 transition-colors"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Sports
              </Link>
              
              {isAuthenticated() ? (
                <>
                  <Link 
                    to="/my-bookings" 
                    className="block text-gray-600 hover:text-blue-600 transition-colors"
                    onClick={() => setIsMobileMenuOpen(false)}
                  >
                    My Bookings
                  </Link>
                  <Link 
                    to="/profile" 
                    className="block text-gray-600 hover:text-blue-600 transition-colors"
                    onClick={() => setIsMobileMenuOpen(false)}
                  >
                    Profile
                  </Link>
                  {user?.roles?.some(role => role.name === 'ROLE_ADMIN') && (
                    <Link 
                      to="/admin" 
                      className="block text-gray-600 hover:text-blue-600 transition-colors"
                      onClick={() => setIsMobileMenuOpen(false)}
                    >
                      Admin Dashboard
                    </Link>
                  )}
                  <button 
                    onClick={handleLogout}
                    className="block w-full text-left text-gray-600 hover:text-blue-600 transition-colors"
                  >
                    Logout
                  </button>
                </>
              ) : (
                <>
                  <Link 
                    to="/login" 
                    className="block text-blue-600 hover:text-blue-800 font-medium transition-colors"
                    onClick={() => setIsMobileMenuOpen(false)}
                  >
                    Login
                  </Link>
                  <Link 
                    to="/register" 
                    className="block bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors text-center"
                    onClick={() => setIsMobileMenuOpen(false)}
                  >
                    Sign Up
                  </Link>
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
