import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const MovieCard = ({ movie }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleImageError = (e) => {
    e.target.src = '/api/placeholder/300/450';
  };

  const getRatingColor = (rating) => {
    if (rating >= 8) return { 
      backgroundColor: 'var(--accent-success)', 
      color: 'var(--text-primary)',
      boxShadow: 'var(--glow-blue)'
    };
    if (rating >= 6) return { 
      backgroundColor: 'var(--brand-primary)', 
      color: 'var(--text-inverse)',
      boxShadow: 'var(--glow-gold)'
    };
    return { 
      backgroundColor: 'var(--accent-error)', 
      color: 'var(--text-primary)',
      boxShadow: 'var(--glow-red)'
    };
  };

  const formatDuration = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  return (
    <div 
      className="relative group cursor-pointer transform transition-all duration-500 hover:scale-105"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      style={{
        background: 'linear-gradient(145deg, var(--background-secondary), var(--background-tertiary))',
        borderRadius: 'var(--radius-2xl)',
        boxShadow: isHovered ? 'var(--shadow-xl), var(--glow-gold)' : 'var(--shadow-lg)',
        border: '1px solid rgba(255, 215, 0, 0.1)'
      }}
    >
      {/* Cinematic Overlay */}
      <div 
        className="absolute inset-0 bg-gradient-to-t from-black via-transparent to-transparent opacity-60 rounded-2xl transition-opacity duration-500"
        style={{ opacity: isHovered ? 0.8 : 0.4 }}
      />
      
      {/* Movie Poster */}
      <div className="relative overflow-hidden rounded-2xl">
        <img
          src={movie.posterUrl || '/api/placeholder/300/450'}
          alt={movie.title}
          className={`w-full h-96 object-cover transition-all duration-700 ${
            isHovered ? 'scale-110 blur-sm' : 'scale-100'
          }`}
          onError={handleImageError}
        />
        
        {/* Animated Light Streak */}
        <div 
          className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-20 transform -skew-x-12 transition-transform duration-1000"
          style={{
            transform: isHovered 
              ? 'translateX(100%) skewX(-12deg)' 
              : 'translateX(-150%) skewX(-12deg)'
          }}
        />
        
        {/* Rating Badge with Glow */}
        {movie.rating && (
          <div 
            className="absolute top-4 left-4 px-4 py-2 rounded-full text-sm font-bold backdrop-blur-sm transition-all duration-300 hover:scale-110"
            style={{
              ...getRatingColor(movie.rating),
              animation: isHovered ? 'pulse 2s infinite' : 'none'
            }}
          >
            ⭐ {movie.rating}
          </div>
        )}
        
        {/* Status Badge */}
        {movie.isNowShowing && (
          <div 
            className="absolute top-4 right-4 px-3 py-1 rounded-full text-xs font-bold backdrop-blur-sm"
            style={{ 
              background: 'linear-gradient(45deg, var(--accent-error), #FF4081)',
              color: 'var(--text-primary)',
              boxShadow: 'var(--glow-red)'
            }}
          >
            🔴 LIVE
          </div>
        )}
        {movie.isComingSoon && (
          <div 
            className="absolute top-4 right-4 px-3 py-1 rounded-full text-xs font-bold backdrop-blur-sm"
            style={{ 
              background: 'linear-gradient(45deg, var(--brand-primary), #FFA726)',
              color: 'var(--text-inverse)',
              boxShadow: 'var(--glow-gold)'
            }}
          >
            🚀 SOON
          </div>
        )}
        
        {/* Play Button Overlay */}
        <div 
          className={`absolute inset-0 flex items-center justify-center transition-all duration-500 ${
            isHovered ? 'opacity-100 scale-100' : 'opacity-0 scale-75'
          }`}
        >
          <div 
            className="w-20 h-20 bg-gradient-to-r from-yellow-400 to-yellow-600 rounded-full flex items-center justify-center shadow-2xl"
            style={{ boxShadow: 'var(--glow-gold)' }}
          >
            <div className="w-0 h-0 border-l-8 border-r-0 border-t-6 border-b-6 border-l-black border-t-transparent border-b-transparent ml-1"></div>
          </div>
        </div>
      </div>

      {/* Movie Details */}
      <div style={{ padding: 'var(--space-lg)' }}>
        <h3 className="heading-3 mb-3 overflow-hidden" style={{ fontSize: 'var(--font-size-lg)' }}>
          <span className="block truncate">{movie.title}</span>
        </h3>
        
        <div className="flex flex-wrap gap-2 mb-4">
          {movie.genre && movie.genre.split(',').map((g, index) => (
            <span
              key={index}
              className="px-3 py-1 rounded-full text-xs font-medium"
              style={{ 
                backgroundColor: 'var(--neutral-100)', 
                color: 'var(--text-secondary)',
                border: '1px solid var(--neutral-200)'
              }}
            >
              {g.trim()}
            </span>
          ))}
        </div>

        {/* Movie Info */}
        <div className="text-sm space-y-2" style={{ color: 'var(--text-secondary)' }}>
          {movie.language && (
            <div className="flex items-center">
              <span className="font-medium" style={{ color: 'var(--text-primary)' }}>Language:</span>
              <span className="ml-2">{movie.language}</span>
            </div>
          )}
          
          {movie.durationMinutes && (
            <div className="flex items-center">
              <span className="font-medium" style={{ color: 'var(--text-primary)' }}>Duration:</span>
              <span className="ml-2">{formatDuration(movie.durationMinutes)}</span>
            </div>
          )}
          
          {movie.releaseDate && (
            <div className="flex items-center">
              <span className="font-medium" style={{ color: 'var(--text-primary)' }}>Release:</span>
              <span className="ml-2">{new Date(movie.releaseDate).toLocaleDateString()}</span>
            </div>
          )}
        </div>

        {/* Description */}
        {movie.description && (
          <p className="text-sm mt-4 h-12 overflow-hidden" style={{ color: 'var(--text-secondary)' }}>
            {movie.description.length > 100 
              ? `${movie.description.substring(0, 100)}...` 
              : movie.description}
          </p>
        )}

        {/* Action Buttons */}
        <div className="mt-6 flex gap-3">
          <Link
            to={`/movies/${movie.id}`}
            className="btn-secondary flex-1 text-center"
            style={{ textDecoration: 'none' }}
          >
            View Details
          </Link>
          
          {movie.isNowShowing && (
            <Link
              to={`/book/${movie.id}`}
              className="btn-primary flex-1 text-center"
              style={{ textDecoration: 'none' }}
            >
              Book Now
            </Link>
          )}
        </div>
      </div>
    </div>
  );
};

export default MovieCard;
