import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { 
  CalendarIcon, 
  TicketIcon, 
  FilmIcon,
  CurrencyRupeeIcon,
  CheckCircleIcon,
  ChevronRightIcon,
  PlayIcon
} from '@heroicons/react/24/outline';
import bookingService from '../../services/bookingService';
import AnimatedCounter from '../../components/common/AnimatedCounter';

const UserDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [userStats, setUserStats] = useState({
    totalBookings: 0,
    upcomingShows: 0,
    completedBookings: 0,
    totalSpent: 0,
    favoriteGenres: []
  });

  const loadUserBookings = async () => {
    try {
      setLoading(true);
      const userBookings = await bookingService.getUserBookings();
      setBookings(userBookings);
      
      const validBookings = Array.isArray(userBookings) ? userBookings : [];
      
      const now = new Date();
      const upcoming = validBookings.filter(booking => {
        const showDate = new Date(booking.show?.showDate || booking.date);
        return showDate > now;
      }).length;
      
      const completed = validBookings.filter(booking => {
        const showDate = new Date(booking.show?.showDate || booking.date);
        return showDate <= now;
      }).length;

      const totalSpent = validBookings.reduce((sum, booking) => {
        return sum + (booking.totalPrice || booking.totalAmount || 0);
      }, 0);

      setUserStats({
        totalBookings: validBookings.length,
        upcomingShows: upcoming,
        completedBookings: completed,
        totalSpent: totalSpent,
        favoriteGenres: extractFavoriteGenres(validBookings)
      });
      
    } catch (err) {
      console.error('Error loading bookings:', err);
      
      const demoBookings = JSON.parse(localStorage.getItem('demoBookings') || '[]');
      const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
      const userBookings = demoBookings.filter(booking => booking.userId === currentUser.id);
      
      setUserStats({
        totalBookings: userBookings.length,
        upcomingShows: userBookings.length,
        completedBookings: 0,
        totalSpent: userBookings.reduce((sum, booking) => sum + (booking.totalAmount || 0), 0),
        favoriteGenres: ['Action', 'Drama', 'Comedy']
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadUserBookings();
    
    const handleBookingCreated = () => {
      console.log('UserDashboard - Booking created event received');
      loadUserBookings();
    };
    
    const handleFocus = () => {
      loadUserBookings();
    };
    
    const handleVisibilityChange = () => {
      if (!document.hidden) {
        loadUserBookings();
      }
    };

    window.addEventListener('bookingCreated', handleBookingCreated);
    window.addEventListener('focus', handleFocus);
    document.addEventListener('visibilitychange', handleVisibilityChange);
    
    return () => {
      window.removeEventListener('bookingCreated', handleBookingCreated);
      window.removeEventListener('focus', handleFocus);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [loadUserBookings]);

  const extractFavoriteGenres = (bookings) => {
    const genreCount = {};
    bookings.forEach(booking => {
      const genre = booking.show?.movie?.genre || booking.genre || 'Unknown';
      genreCount[genre] = (genreCount[genre] || 0) + 1;
    });
    
    return Object.entries(genreCount)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 3)
      .map(([genre]) => genre);
  };

  // Mock data for Now Showing
  const nowShowingMovies = [
    {
      id: 1,
      title: 'DUNE',
      subtitle: 'PART TWO',
      genre: 'Adventure',
      image: '/api/placeholder/300/400',
      ticketsLeft: '50%',
      trailer: true
    },
    {
      id: 2,
      title: 'GODZILLA X KONG',
      subtitle: 'THE NEW EMPIRE',
      genre: 'Action',
      image: '/api/placeholder/300/400',
      ticketsLeft: '80%',
      trailer: true
    },
    {
      id: 3,
      title: 'DEADPOOL',
      subtitle: 'WOLVERINE',
      genre: 'Action',
      image: '/api/placeholder/300/400',
      ticketsLeft: '30%',
      trailer: true
    },
    {
      id: 4,
      title: 'DUNE',
      subtitle: 'PART TWO',
      genre: 'Adventure',
      image: '/api/placeholder/300/400',
      ticketsLeft: '90%',
      trailer: true
    }
  ];

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-4 border-red-500 border-t-transparent mx-auto mb-4"></div>
          <p className="text-white">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Main Container */}
      <div className="flex">
        {/* Sidebar */}
        <div className="w-64 bg-gray-800 min-h-screen p-6">
          {/* User Profile Section */}
          <div className="mb-8">
            <div className="flex items-center space-x-3 mb-6">
              <div className="w-12 h-12 bg-red-500 rounded-full flex items-center justify-center">
                <span className="text-white font-bold text-lg">
                  {user?.name ? user.name.charAt(0).toUpperCase() : 'A'}
                </span>
              </div>
              <div>
                <h3 className="text-white font-semibold">Welcome back,</h3>
                <p className="text-red-400 text-sm">{user?.email || 'ash@gmail.com'}</p>
              </div>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="mb-8">
            <h3 className="text-gray-400 text-sm font-semibold mb-4 uppercase tracking-wider">Quick Actions</h3>
            <div className="space-y-3">
              <button 
                onClick={() => navigate('/movies')}
                className="w-full flex items-center space-x-3 p-3 rounded-lg bg-red-600 hover:bg-red-700 transition-colors"
              >
                <div className="w-8 h-8 bg-white bg-opacity-20 rounded-lg flex items-center justify-center">
                  <FilmIcon className="h-5 w-5" />
                </div>
                <span className="font-medium">Browse Movies</span>
                <ChevronRightIcon className="h-4 w-4 ml-auto" />
              </button>
              
              <button 
                onClick={() => navigate('/my-bookings')}
                className="w-full flex items-center space-x-3 p-3 rounded-lg bg-gray-700 hover:bg-gray-600 transition-colors"
              >
                <div className="w-8 h-8 bg-cyan-500 bg-opacity-20 rounded-lg flex items-center justify-center">
                  <TicketIcon className="h-5 w-5 text-cyan-400" />
                </div>
                <span className="font-medium">My Bookings</span>
                <ChevronRightIcon className="h-4 w-4 ml-auto" />
              </button>

              <button 
                onClick={() => navigate('/payments')}
                className="w-full flex items-center space-x-3 p-3 rounded-lg bg-gray-700 hover:bg-gray-600 transition-colors"
              >
                <div className="w-8 h-8 bg-purple-500 bg-opacity-20 rounded-lg flex items-center justify-center">
                  <CurrencyRupeeIcon className="h-5 w-5 text-purple-400" />
                </div>
                <span className="font-medium">Payments & Wallet</span>
                <ChevronRightIcon className="h-4 w-4 ml-auto" />
              </button>
            </div>
          </div>

          {/* Dashboard Modules */}
          <div>
            <h3 className="text-gray-400 text-sm font-semibold mb-4 uppercase tracking-wider">Dashboard Modules</h3>
            <div className="space-y-2">
              <button 
                onClick={() => navigate('/movies')}
                className="w-full flex items-center justify-between p-3 rounded-lg bg-gray-700 hover:bg-gray-600 transition-colors"
              >
                <div className="flex items-center space-x-3">
                  <FilmIcon className="h-5 w-5 text-gray-400" />
                  <span>Browse Movies</span>
                </div>
                <div className="bg-red-500 text-white text-xs px-2 py-1 rounded-full">
                  BOOK NOW
                </div>
              </button>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 p-8">
          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            {/* Total Bookings */}
            <div className="bg-gray-800 rounded-xl p-6 border border-gray-700">
              <div className="flex items-center justify-between mb-4">
                <div className="w-12 h-12 bg-red-500 rounded-xl flex items-center justify-center">
                  <TicketIcon className="h-6 w-6 text-white" />
                </div>
              </div>
              <div className="text-3xl font-bold text-white mb-1">
                <AnimatedCounter end={userStats.totalBookings} duration={2000} />
              </div>
              <div className="text-gray-400 text-sm">TOTAL BOOKINGS</div>
            </div>

            {/* Upcoming Shows */}
            <div className="bg-gray-800 rounded-xl p-6 border border-gray-700">
              <div className="flex items-center justify-between mb-4">
                <div className="w-12 h-12 bg-cyan-500 rounded-xl flex items-center justify-center">
                  <CalendarIcon className="h-6 w-6 text-white" />
                </div>
              </div>
              <div className="text-3xl font-bold text-white mb-1">
                <AnimatedCounter end={userStats.upcomingShows} duration={2000} />
              </div>
              <div className="text-gray-400 text-sm">UPCOMING SHOWS</div>
            </div>

            {/* Completed */}
            <div className="bg-gray-800 rounded-xl p-6 border border-gray-700">
              <div className="flex items-center justify-between mb-4">
                <div className="w-12 h-12 bg-green-500 rounded-xl flex items-center justify-center">
                  <CheckCircleIcon className="h-6 w-6 text-white" />
                </div>
              </div>
              <div className="text-3xl font-bold text-white mb-1">
                <AnimatedCounter end={userStats.completedBookings} duration={2000} />
              </div>
              <div className="text-gray-400 text-sm">COMPLETED</div>
            </div>

            {/* Total Spent */}
            <div className="bg-gray-800 rounded-xl p-6 border border-gray-700">
              <div className="flex items-center justify-between mb-4">
                <div className="w-12 h-12 bg-orange-500 rounded-xl flex items-center justify-center">
                  <CurrencyRupeeIcon className="h-6 w-6 text-white" />
                </div>
              </div>
              <div className="text-3xl font-bold text-white mb-1">
                <AnimatedCounter end={userStats.totalSpent} duration={2000} />
              </div>
              <div className="text-gray-400 text-sm">TOTAL SPENT</div>
            </div>
          </div>

          {/* Movie Details Card */}
          <div className="bg-gray-800 rounded-xl p-6 border border-gray-700 mb-8">
            <div className="flex items-center justify-between mb-6">
              <div>
                <h2 className="text-2xl font-bold text-white mb-2">Betalcle</h2>
                <p className="text-gray-400">Vestige Directore</p>
                <p className="text-gray-400">Oeche hartiont</p>
              </div>
              <div className="flex space-x-4">
                <button className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg text-white text-sm font-medium transition-colors">
                   TRAILER
                </button>
                <button className="px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg text-gray-300 text-sm font-medium transition-colors">
                   TRAILER
                </button>
              </div>
            </div>
            <div className="mb-4">
              <div className="text-sm text-gray-400 mb-2">TICKETS LEFT: 80%</div>
              <div className="w-full bg-gray-700 rounded-full h-2">
                <div className="bg-green-500 h-2 rounded-full" style={{ width: '80%' }}></div>
              </div>
            </div>
            <div className="flex space-x-4">
              <button className="flex-1 bg-red-600 hover:bg-red-700 text-white py-3 rounded-lg font-medium transition-colors">
                 TRAILER
              </button>
            </div>
          </div>

          {/* Now Showing Section */}
          <div className="mb-8">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-2xl font-bold text-white">Now Showing</h2>
              <div className="flex space-x-2">
                <button className="px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg text-gray-300 text-sm transition-colors">
                  Action
                </button>
                <button className="px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg text-gray-300 text-sm transition-colors">
                  Comedy
                </button>
                <button className="px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg text-gray-300 text-sm transition-colors">
                  Horror
                </button>
                <button className="px-4 py-2 bg-red-600 hover:bg-red-700 rounded-lg text-white text-sm transition-colors">
                  Action
                </button>
              </div>
            </div>

            {/* Movie Grid */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
              {nowShowingMovies.map((movie) => (
                <div key={movie.id} className="bg-gray-800 rounded-xl overflow-hidden border border-gray-700 group hover:border-red-500 transition-colors cursor-pointer"
                     onClick={() => navigate(`/movies/${movie.id}`)}>
                  <div className="aspect-[3/4] bg-gradient-to-b from-gray-700 to-gray-800 relative">
                    {/* Placeholder for movie poster */}
                    <div className="absolute inset-0 flex items-center justify-center">
                      <FilmIcon className="h-16 w-16 text-gray-600" />
                    </div>
                    {/* Play button overlay */}
                    <div className="absolute inset-0 bg-black bg-opacity-50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
                      <div className="w-16 h-16 bg-red-500 rounded-full flex items-center justify-center">
                        <PlayIcon className="h-8 w-8 text-white ml-1" />
                      </div>
                    </div>
                    {/* Book Now Button */}
                    <div className="absolute top-3 right-3">
                      <button 
                        className="bg-red-500 hover:bg-red-600 text-white text-xs px-2 py-1 rounded-full transition-colors"
                        onClick={(e) => {
                          e.stopPropagation();
                          navigate('/movies');
                        }}
                      >
                        BOOK NOW
                      </button>
                    </div>
                  </div>
                  <div className="p-4">
                    <h3 className="font-bold text-white text-sm mb-1">{movie.title}</h3>
                    <p className="text-gray-400 text-xs mb-2">{movie.subtitle}</p>
                    <div className="flex items-center justify-between">
                      <span className="text-gray-400 text-xs">{movie.genre}</span>
                      {movie.trailer && (
                        <span className="text-red-400 text-xs"> TRAILER</span>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
