import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  HeartIcon as HeartOutline, 
  StarIcon,
  CalendarIcon,
  ClockIcon,
  MapPinIcon,
  FilmIcon,
  MagnifyingGlassIcon
} from '@heroicons/react/24/outline';
import { HeartIcon as HeartSolid } from '@heroicons/react/24/solid';
import movieService from '../services/movieService';
import bookingService from '../services/bookingService';

const Movies = () => {
  const navigate = useNavigate();
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [wishlist, setWishlist] = useState(new Set());
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('all');

  const fetchMovies = useCallback(async (currentFilters = filters, currentPage = pagination.page) => {
    setLoading(true);
    setError(null);
    
    try {
      const filterParams = {
        ...currentFilters,
        page: currentPage,
        size: pagination.size
      };

      console.log('Fetching movies with params:', filterParams);
      const response = await movieService.getMovies(filterParams);
      console.log('Movies response:', response);
      
      setMovies(response.content || []);
      setPagination({
        page: response.pageable?.pageNumber || 0,
        size: response.pageable?.pageSize || 12,
        totalElements: response.totalElements || 0,
        totalPages: response.totalPages || 0
      });
    } catch (err) {
      setError('Failed to load movies. Please try again later.');
      console.error('Error fetching movies:', err);
    } finally {
      setLoading(false);
    }
  }, []); // Remove dependencies to prevent infinite re-renders

  useEffect(() => {
    fetchMovies(filters, pagination.page);
  }, []); // Only run once on component mount

  const handleFiltersChange = (newFilters) => {
    setFilters(newFilters);
    fetchMovies(newFilters, 0); // Reset to first page when filters change
  };

  const handlePageChange = (newPage) => {
    fetchMovies(filters, newPage);
  };

  const LoadingSkeleton = () => (
    <div className="grid-auto">
      {[...Array(8)].map((_, index) => (
        <div key={index} className="card-simple loading-pulse fade-in">
          <div className="h-80" style={{ backgroundColor: 'var(--neutral-200)', borderRadius: 'var(--radius-md)' }}></div>
          <div style={{ padding: 'var(--space-lg)' }}>
            <div className="h-4 rounded w-3/4 mb-2" style={{ backgroundColor: 'var(--neutral-200)' }}></div>
            <div className="h-3 rounded w-1/2 mb-2" style={{ backgroundColor: 'var(--neutral-200)' }}></div>
            <div className="h-3 rounded w-full mb-2" style={{ backgroundColor: 'var(--neutral-200)' }}></div>
            <div className="h-8 rounded w-full mt-4" style={{ backgroundColor: 'var(--neutral-200)' }}></div>
          </div>
        </div>
      ))}
    </div>
  );

  return (
    <div 
      className="min-h-screen relative"
      style={{ 
        background: 'linear-gradient(135deg, var(--background-primary) 0%, var(--background-secondary) 100%)',
      }}
    >
      {/* Cinematic Background Effects */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-10 -right-10 w-96 h-96 bg-red-400/5 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute -bottom-10 -left-10 w-96 h-96 bg-cyan-400/5 rounded-full blur-3xl animate-pulse" style={{ animationDelay: '2s' }}></div>
      </div>
      
      <div className="container-main py-12 relative z-10">
        {/* Header */}
        <div className="mb-16 slide-up text-center">
          <h1 
            className="text-6xl font-bold bg-gradient-to-r from-red-400 via-red-500 to-red-600 bg-clip-text text-transparent mb-6"
            style={{ 
              textShadow: '0 0 50px rgba(255, 215, 0, 0.3)',
              fontFamily: "'Cinzel', serif"
            }}
          >
            Cinema Collection
          </h1>
          <p className="text-2xl text-gray-300 max-w-2xl mx-auto leading-relaxed">
            Discover blockbuster adventures and cinematic masterpieces
          </p>
          <div className="mt-6 w-32 h-1 bg-gradient-to-r from-red-400 to-cyan-400 mx-auto rounded-full"></div>
        </div>

        {/* Filters */}
        <MovieFilters 
          onFiltersChange={handleFiltersChange}
          initialFilters={filters}
        />

        {/* Error State */}
        {error && (
          <div className="card-simple mb-8 fade-in" 
               style={{ 
                 backgroundColor: '#FEF2F2', 
                 border: '1px solid var(--error)' 
               }}>
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5" style={{ color: 'var(--error)' }} viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3 flex-1">
                <h3 className="text-sm font-medium" style={{ color: 'var(--error)' }}>Error</h3>
                <div className="mt-2 text-sm" style={{ color: 'var(--error)' }}>
                  <p>{error}</p>
                </div>
                <div className="mt-4">
                  <button
                    onClick={() => fetchMovies()}
                    className="status-error cursor-pointer"
                  >
                    Try Again
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Movies Grid */}
        {loading ? (
          <LoadingSkeleton />
        ) : movies.length > 0 ? (
          <>
            {/* Results Summary */}
            <div className="mb-8 fade-in">
              <p className="text-muted">
                Showing {pagination.totalElements} movie{pagination.totalElements !== 1 ? 's' : ''}
                {filters.search && ` matching "${filters.search}"`}
                {filters.genre && ` in ${filters.genre}`}
                {filters.language && ` in ${filters.language}`}
              </p>
            </div>

            {/* Movies Grid */}
            <div className="grid-auto mb-12">
              {movies.map((movie) => (
                <MovieCard key={movie.id} movie={movie} />
              ))}
            </div>

            {/* Pagination */}
            <Pagination
              currentPage={pagination.page}
              totalPages={pagination.totalPages}
              totalElements={pagination.totalElements}
              size={pagination.size}
              onPageChange={handlePageChange}
            />
          </>
        ) : (
          /* Empty State */
          <div className="text-center py-16 fade-in">
            <div className="text-8xl mb-6">🎬</div>
            <h3 className="heading-3 mb-4">No movies found</h3>
            <p className="text-muted mb-8">
              Try adjusting your search or filter criteria.
            </p>
            <div>
              <button
                onClick={() => handleFiltersChange({
                  search: '',
                  genre: '',
                  language: '',
                  rating: '',
                  nowShowing: null,
                  comingSoon: null,
                  sortBy: 'createdAt',
                  sortDirection: 'desc'
                })}
                className="btn-primary"
              >
                Clear Filters
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Movies;
