import React, { useState, useEffect } from 'react';

const AnimatedCounter = ({ 
  endValue, 
  duration = 1500, 
  prefix = '', 
  suffix = '', 
  formatter = null 
}) => {
  const [currentValue, setCurrentValue] = useState(0);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Start animation when component becomes visible
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !isVisible) {
          setIsVisible(true);
        }
      },
      { threshold: 0.1 }
    );

    const element = document.getElementById(`counter-${Math.random()}`);
    if (element) {
      observer.observe(element);
    }

    return () => {
      if (element) {
        observer.unobserve(element);
      }
    };
  }, [isVisible]);

  useEffect(() => {
    if (!isVisible) return;

    let startTime;
    const startValue = 0;
    const endVal = typeof endValue === 'string' ? parseInt(endValue) || 0 : endValue;

    const animate = (currentTime) => {
      if (!startTime) startTime = currentTime;
      
      const elapsed = currentTime - startTime;
      const progress = Math.min(elapsed / duration, 1);
      
      // Easing function for smooth animation
      const easeOut = 1 - Math.pow(1 - progress, 3);
      const value = Math.floor(startValue + (endVal - startValue) * easeOut);
      
      setCurrentValue(value);
      
      if (progress < 1) {
        requestAnimationFrame(animate);
      }
    };

    requestAnimationFrame(animate);
  }, [isVisible, endValue, duration]);

  const formatValue = (value) => {
    if (formatter) {
      return formatter(value);
    }
    return value.toLocaleString();
  };

  return (
    <span 
      id={`counter-${Math.random()}`}
      className="inline-block"
      style={{ 
        fontVariantNumeric: 'tabular-nums',
        transition: 'color var(--transition-fast)' 
      }}
    >
      {prefix}{formatValue(currentValue)}{suffix}
    </span>
  );
};

export default AnimatedCounter;
