import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { 
  CalendarIcon, 
  TicketIcon, 
  FilmIcon,
  ClockIcon,
  MapPinIcon,
  CurrencyRupeeIcon,
  CheckCircleIcon,
  XCircleIcon,
  MagnifyingGlassIcon
} from '@heroicons/react/24/outline';
import bookingService from '../../services/bookingService';

const MyBookings = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');

  useEffect(() => {
    loadBookings();
  }, []);

  const loadBookings = async () => {
    try {
      setLoading(true);
      const data = await bookingService.getUserBookings();
      
      // Handle both paginated and direct array responses
      const bookingsArray = Array.isArray(data) ? data : (data.content || []);
      setBookings(bookingsArray);
      
    } catch (err) {
      console.error('Error loading bookings:', err);
      setError('Failed to load bookings');
      // Use sample data for demonstration
      const sampleBookings = [
        {
          id: 1,
          bookingReference: 'BK001',
          show: {
            movie: { title: 'Spider-Man: No Way Home', posterUrl: '/placeholder-movie.jpg' },
            venue: { name: 'PVR Cinemas Forum Mall', address: '123 Forum Mall' },
            showDate: '2025-10-20',
            showTime: '19:30'
          },
          seats: [{ seatNumber: 'F12' }, { seatNumber: 'F13' }],
          totalAmount: 500,
          status: 'CONFIRMED',
          bookingDate: '2025-10-15T10:00:00Z'
        },
        {
          id: 2,
          bookingReference: 'BK002',
          show: {
            movie: { title: 'Dune: Part Two', posterUrl: '/placeholder-movie.jpg' },
            venue: { name: 'INOX Garuda Mall', address: '456 Garuda Mall' },
            showDate: '2025-10-18',
            showTime: '16:00'
          },
          seats: [{ seatNumber: 'A5' }],
          totalAmount: 250,
          status: 'COMPLETED',
          bookingDate: '2025-10-12T14:30:00Z'
        }
      ];
      setBookings(sampleBookings);
    } finally {
      setLoading(false);
    }
  };

  const filteredBookings = bookings.filter(booking => {
    const matchesSearch = booking.show?.movie?.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         booking.show?.venue?.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         booking.bookingReference?.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesFilter = filterStatus === 'all' || booking.status === filterStatus;
    
    return matchesSearch && matchesFilter;
  });

  const BookingCard = ({ booking }) => (
    <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-blue-500 hover:shadow-lg transition-shadow">
      <div className="flex justify-between items-start mb-4">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">
            {booking.show?.movie?.title || 'Movie Title'}
          </h3>
          <p className="text-gray-600 text-sm">Booking Reference: {booking.bookingReference}</p>
        </div>
        <span className={`px-3 py-1 rounded-full text-xs font-medium ${
          booking.status === 'CONFIRMED' 
            ? 'bg-green-100 text-green-800' 
            : booking.status === 'COMPLETED'
            ? 'bg-blue-100 text-blue-800'
            : 'bg-gray-100 text-gray-800'
        }`}>
          {booking.status}
        </span>
      </div>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <h4 className="font-medium text-gray-900 mb-2">Show Details</h4>
          <div className="space-y-1 text-sm text-gray-600">
            <div className="flex items-center">
              <CalendarIcon className="h-4 w-4 mr-2" />
              {booking.show?.showDate || 'Date'}
            </div>
            <div className="flex items-center">
              <ClockIcon className="h-4 w-4 mr-2" />
              {booking.show?.showTime || 'Time'}
            </div>
            <div className="flex items-center">
              <MapPinIcon className="h-4 w-4 mr-2" />
              {booking.show?.venue?.name || 'Venue'}
            </div>
          </div>
        </div>
        
        <div>
          <h4 className="font-medium text-gray-900 mb-2">Booking Details</h4>
          <div className="space-y-1 text-sm text-gray-600">
            <div className="flex items-center">
              <TicketIcon className="h-4 w-4 mr-2" />
              Seats: {Array.isArray(booking.seats) 
                ? booking.seats.map(seat => seat.seatNumber || seat).join(', ')
                : 'N/A'}
            </div>
            <div className="flex items-center">
              <CurrencyRupeeIcon className="h-4 w-4 mr-2" />
              Total: ₹{booking.totalAmount || 0}
            </div>
            <div className="flex items-center">
              <CalendarIcon className="h-4 w-4 mr-2" />
              Booked: {new Date(booking.bookingDate).toLocaleDateString()}
            </div>
          </div>
        </div>
      </div>
      
      <div className="mt-4 pt-4 border-t border-gray-200 flex justify-between items-center">
        <button 
          onClick={() => navigate(`/booking/${booking.id}`)}
          className="text-blue-600 hover:text-blue-800 text-sm font-medium"
        >
          View Details
        </button>
        {booking.status === 'CONFIRMED' && (
          <button className="bg-red-100 text-red-800 px-3 py-1 rounded text-xs font-medium hover:bg-red-200 transition-colors">
            Cancel Booking
          </button>
        )}
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading your bookings...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">My Bookings</h1>
              <p className="text-gray-600 mt-1">View and manage all your movie bookings</p>
            </div>
            <button
              onClick={() => navigate('/dashboard')}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              Back to Dashboard
            </button>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {error && (
          <div className="mb-6 bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <p className="text-yellow-700">{error} - Showing sample data</p>
          </div>
        )}

        {/* Search and Filter */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between space-y-4 md:space-y-0">
            <div className="relative flex-1 max-w-md">
              <MagnifyingGlassIcon className="h-5 w-5 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <input
                type="text"
                placeholder="Search bookings..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 pr-4 py-2 w-full border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
            
            <div className="flex items-center space-x-4">
              <select
                value={filterStatus}
                onChange={(e) => setFilterStatus(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="all">All Status</option>
                <option value="CONFIRMED">Confirmed</option>
                <option value="COMPLETED">Completed</option>
                <option value="CANCELLED">Cancelled</option>
              </select>
            </div>
          </div>
        </div>

        {/* Bookings List */}
        <div className="space-y-6">
          {filteredBookings.length > 0 ? (
            filteredBookings.map(booking => (
              <BookingCard key={booking.id} booking={booking} />
            ))
          ) : (
            <div className="text-center py-12">
              <TicketIcon className="h-16 w-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No Bookings Found</h3>
              <p className="text-gray-600 mb-6">
                {searchTerm || filterStatus !== 'all' 
                  ? 'Try adjusting your search or filter criteria.'
                  : 'You haven\'t made any bookings yet.'}
              </p>
              <button
                onClick={() => navigate('/movies')}
                className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors"
              >
                Browse Movies
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MyBookings;
